import React, { Component } from "react";
import {InfoWindow, GoogleApiWrapper} from 'google-maps-react';



export class MoreDetails extends Component {


  state={
    lat: false,
    lng: '',
    data: false,
    clientX: '',
    clientY: ''
  }

  componentDidMount(){

        this.interval = setInterval(() => this.setState({ time: Date.now() }), 500);

    }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  renderNo2 = () => {
    if (this.state.data !== false && this.state.data.iaqi.hasOwnProperty('no2')){
      return <p>{this.state.data.iaqi.no2.v}</p>
    } else {
      return <p>-</p>
    }
  }

  fetchData = () => {
    if (this.state.lat){
      fetch(`https://api.waqi.info/feed/geo:${this.state.lat};${this.state.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
      .then(resp => resp.json())
      .then(data => {
        this.setState({
          data: data.data
        })
      })
    } else {
      return null
    }
  }

  render(){
    const style = {
      position: 'absolute',
      top: '1rem',
      left: '50%',
      transform: 'translateX(-50%)',
      width: '100%',
      zindex: '10',
      color: '#000080'
    }
    console.log(this.state.clientX)
    this.fetchData()
    return <div style={style}>
      {/* {this.state.data ? this.state.data.iaqi.dew.v : <p>-</p>} */}
      {this.renderNo2()}
    </div>
  }
}



export default MoreDetails
