import React, { Component } from "react";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import 'react-google-places-autocomplete/dist/index.min.css';
import Geocode from "react-geocode";
import ClimateScore from './ClimateScore'

export class ClimateMap extends Component {

  state = {
    searchText: '',
    showClimateScore: false,
    coords: {
      lat: '40.6499541',
      lng: '-87.8512693'
    },
    allScores: {
      ClimateScore: 33,
      DroughtScore: 67,
      FireScore: 33,
      SeaLevelScore: 0,
      StormScore: 63,
      TempScore: 1
    } 
  }

  mapCLicked = (mapProps, map, event) => {
    console.log('I was clicked!')
    console.log(mapProps)
    console.log(map)
    console.log(event)
  }


  onMarkerDragEnd = (coord) => {
    const { latLng } = coord;
    const newLat = latLng.lat();
    const newLon = latLng.lng();
    fetch(`https://climate-score.p.rapidapi.com/${newLat}/${newLon}`, {
    	"method": "GET",
    	"headers": {
	  	"x-rapidapi-host": "climate-score.p.rapidapi.com",
	  	"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
      }
    })
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
      this.setState({
        allScores: {
          ClimateScore: data.ClimateScore,
          DroughtScore: data.DroughtScore,
          FireScore: data.FireScore,
          SeaLevelScore: data.SeaLevelScore,
          StormScore: data.StormScore,
          TempScore: data.TempScore,
        },
      })
    })
    .catch(err => {
      this.setState({
        allScores: false,
      })
      console.log(err)})
    this.setState({
      coords: {
        lat: newLat,
        lng: newLon
      }
    })
  };

  handleChange = (e) => {
    this.setState({
      searchText: e.target.value
    })

  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log(event)
    Geocode.setApiKey("AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8");
    Geocode.fromAddress(`${event.target.firstElementChild.value}`).then(
      response => {
      const { lat, lng } = response.results[0].geometry.location;
      console.log(lat, lng);
      fetch(`https://climate-score.p.rapidapi.com/${lat}/${lng}`, {
        "method": "GET",
        "headers": {
        "x-rapidapi-host": "climate-score.p.rapidapi.com",
        "x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
        }
      })
      .then(resp => resp.json())
      .then(data => {
        console.log(data)
        this.setState({
          allScores: {
            ClimateScore: data.ClimateScore,
            DroughtScore: data.DroughtScore,
            FireScore: data.FireScore,
            SeaLevelScore: data.SeaLevelScore,
            StormScore: data.StormScore,
            TempScore: data.TempScore,
          },
        })
      })
      .catch(err => {
        this.setState({
          allScores: false,
        })
        console.log(err)})
      this.setState({
        coords: {
          lat: lat,
          lng: lng
        }
      })
      },
      error => {
      console.error(error);
    })
    this.setState({
      searchText: ''
    })
  }

  render() {
    console.log(this.state.allScores)
    console.log(this.state.coords)
    const style = {
      width: '90%',
      height: '90%'
    }
    return (
      <div>
      <ClimateScore coords={this.state.coords} scores={this.state.allScores} />
        {/* <form onSubmit={this.handleSubmit}>
            <input type='text' value={this.state.text} onChange={this.handleChange} />
            <input type='submit' value='Search!'/>
        </form> */}
        <GooglePlacesAutocomplete onSelect={({ value }) => {
          this.handleSubmit
          (this.setState({ searchText: value }))}}
        />
      <Map  google={this.props.google}
            style={style} 
            initialCenter={this.state.coords}
            center={this.state.coords}
            onClick={this.mapCLicked} 
            zoom={14}
            >

        <Marker onClick={this.onMarkerClick}
                name={'Current location'} 
                draggable={true}
                position={this.state.coords}
                onDragend={(t, map, coord) => this.onMarkerDragEnd(coord)}/>

        <InfoWindow onClose={this.onInfoWindowClose}>
        </InfoWindow>
      </Map>
      </div>
    );
  }
}




export default GoogleApiWrapper({
  apiKey: ('AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8')
})(ClimateMap)
