import React, { Component } from "react";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';

export class ClimateMap extends Component {
  state = {
    region: {
      latitude: 37.78825,
      longitude: -122.4324,
      latitudeDelta: 0.0922,
      longitudeDelta: 0.0421,
    }
}

  mapCLicked = (mapProps, map, event) => {
    console.log('I was clicked!')
    console.log(mapProps)
    console.log(map)
    console.log(event)
    return (
      <Marker />
    )
  }


  onMarkerDragEnd = (coord, index) => {
    const { latLng } = coord;
    const lat = latLng.lat();
    const lng = latLng.lng();

    this.setState(prevState => {
      const markers = [...this.state.markers];
      markers[index] = { ...markers[index], position: { lat, lng } };
      return { markers };
    });
  };


  render() {
    return (
      <Map google={this.props.google} onClick={this.mapCLicked} zoom={14}>

        <Marker onClick={this.onMarkerClick}
                name={'Current location'} 
                draggable={true}
                onDragEnd={(e) => this.onMarkerDragEnd(e.nativeEvent.coordinate)}/>

        <InfoWindow onClose={this.onInfoWindowClose}>
        </InfoWindow>
      </Map>
    );
  }
}




export default GoogleApiWrapper({
  apiKey: ('AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8')
})(ClimateMap)
