  // }

  renderMarkers = () =>{
    return this.state.stations.map((statObj)=>{
      console.log('PENIS')
        const google = this.props.google
        let iw = 83,
        ih = 107;
        if (statObj.aqi !== '-'){
          const test = statObj.uid
          return (
            <Marker  info={statObj}
            position={{lat: statObj.lat, lng: statObj.lon}}
            onClick={this.props.onMarkerClick}
            // onMouseover={(e)=>this.pinHover(e)}
            // onMouseout={(e)=>this.mouseOut(e)}
            icon={{
              url: `https://waqi.info/mapicon/${statObj.aqi}.50.png`,
              anchor: new google.maps.Point(iw / 4, ih / 2 - 5),
              size: new google.maps.Size(iw / 2, ih / 2),
              scaledSize: new google.maps.Size(30, 40)
            }}
            />
            )
          }  
        })
  }

  render() {
    console.log(this.state)
    return (<div>
      {this.state.stations !== false ? this.renderMarkers() : null}
      </div>

    )
  }

}

