import React, { Component } from "react";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import 'react-google-places-autocomplete/dist/index.min.css';
import Geocode from "react-geocode";
import ClimateScore from './ClimateScore'


export class ClimateMap extends Component {

  state = {
    searchText: '',
    showClimateScore: false,
    coords: {
      lat: '-23.304354',
      lng: '151.916417'
    },
    address: 'Wilson Island, Australia',
    allScores: false,
    allAqiStations: []
  }

  componentDidMount(){
    navigator.geolocation.getCurrentPosition((position) => {
      let lat = position.coords.latitude
      let lng = position.coords.longitude
      let currentLocation = {lat: lat,lng: lng}
      this.setState({
        coords: currentLocation
      })
    })
    fetch(`https://api.waqi.info/feed/geo:${this.state.coords.lat};${this.state.coords.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
    .then(resp => resp.json())
    .then(data => console.log(data.data))
    fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
    .then(resp => resp.json())
    .then(stations => {
      this.setState({ 
        allAqiStations: stations.data
      })
    })
  }

  mapCLicked = (mapProps, map, event) => {
    console.log('I was clicked!')
    console.log(mapProps)
    console.log(map)
    console.log(event)
  }


  onMarkerDragEnd = (coord) => {
    const { latLng } = coord;
    const newLat = latLng.lat();
    const newLon = latLng.lng();
    fetch(`https://climate-score.p.rapidapi.com/${newLat}/${newLon}`, {
    	"method": "GET",
    	"headers": {
	  	"x-rapidapi-host": "climate-score.p.rapidapi.com",
	  	"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
      }
    })
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
      this.setState({
        allScores: {
          ClimateScore: data.ClimateScore,
          DroughtScore: data.DroughtScore,
          FireScore: data.FireScore,
          SeaLevelScore: data.SeaLevelScore,
          StormScore: data.StormScore,
          TempScore: data.TempScore,
        },
      })
    })
    .catch(err => {
      this.setState({
        allScores: false,
      })
      console.log(err)})
    this.setState({
      coords: {
        lat: newLat,
        lng: newLon
      }
    })
    Geocode.setApiKey("AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8");
    Geocode.fromLatLng(this.state.coords.lat, this.state.coords.lng).then(
      response => {
        const newAddress = response.results[0].formatted_address;
        this.setState({
          address: newAddress
        });
      },
      error => {
        console.error(error);
      }
    );
    fetch(`https://api.waqi.info/feed/geo:${this.state.coords.lat};${this.state.coords.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
    .then(resp => resp.json())
    .then(data => console.log(data.data))  
  };

  handleSubmit = (value) => {
    Geocode.setApiKey("AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8");
    Geocode.fromAddress(`${value.description}`).then(
      response => {
      const { lat, lng } = response.results[0].geometry.location;
      console.log(lat, lng);
      fetch(`https://climate-score.p.rapidapi.com/${lat}/${lng}`, {
        "method": "GET",
        "headers": {
        "x-rapidapi-host": "climate-score.p.rapidapi.com",
        "x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
        }
      })
      .then(resp => resp.json())
      .then(data => {
        console.log(data)
        this.setState({
          allScores: {
            ClimateScore: data.ClimateScore,
            DroughtScore: data.DroughtScore,
            FireScore: data.FireScore,
            SeaLevelScore: data.SeaLevelScore,
            StormScore: data.StormScore,
            TempScore: data.TempScore,
          },
        })
      })
      .catch(err => {
        this.setState({
          allScores: false,
        })
        console.log(err)})
      Geocode.setApiKey("AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8");
      Geocode.fromLatLng(lat, lng).then(
      response => {
        const newAddress = response.results[0].formatted_address;
        this.setState({
          address: newAddress
        });
      },
      error => {
        console.error(error);
      });
      this.setState({
        coords: {
          lat: lat,
          lng: lng
        }
      })
      fetch(`https://api.waqi.info/feed/geo:${this.state.coords.lat};${this.state.coords.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
      .then(resp => resp.json())
      .then(data => console.log(data.data))  
      },
      error => {
      console.error(error);
    })
  }

  renderMarkers = () => {
    console.log('working')
    return this.state.allAqiStations.map((statObj)=>{
      const google = this.props.google
      return <Marker  position={{lat: statObj.lat, lng: statObj.lon}}
                      icon={{
                        url: 'https://cdn3.iconfinder.com/data/icons/camill-icons/256/camill_globe.png',
                        anchor: new google.maps.Point(5, 58),
                      }}


      />
    })
  }

  render() {
    const style = {
      width: '90%',
      height: '90%'
    }
    return (
      <div>
      <ClimateScore coords={this.state.coords} scores={this.state.allScores} address={this.state.address} />
        <GooglePlacesAutocomplete onSelect={({ value }) => {(this.setState({ searchText: value }))}}
                                  onSelect={this.handleSubmit}
        />
      <Map  google={this.props.google}
            style={style} 
            initialCenter={this.state.coords}
            center={this.state.coords}
            onClick={this.mapCLicked} 
            zoom={14}
            >
        {this.renderMarkers()}
        <Marker 
          position={{lat: 56.999, lng: 55.888}}
          />
        <Marker onClick={this.onMarkerClick}
                name={'Current location'} 
                draggable={true}
                initialCenter={this.state.coords}
                position={this.state.coords}
                onDragend={(t, map, coord) => this.onMarkerDragEnd(coord)}/>

        {/* <InfoWindow onClose={this.onInfoWindowClose}>
        </InfoWindow> */}
      </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8')
})(ClimateMap)
