import React from 'react';
import { connect } from 'react-redux'
import { fetchUsersSuccess } from '../actions/user'
import { fetchStatSuccess } from '../actions/stat'
import ClimateMap from './ClimateMap'


class Dashboard extends React.Component {
  componentDidMount(){
      fetch('http://localhost:3001/users')
      .then(resp => resp.json())
      .then(data => {
        this.props.fetchUsersSuccess(data)
      })
      fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
      .then(resp => resp.json())
      .then(stations => {
        this.props.fetchStatSuccess(stations.data)
      })
  }

  myCallback = (dataFromChild) => {
    return <h1>{dataFromChild}</h1>
  }

  render(){ 
    return (
      <div>
        {this.myCallback}
        <ClimateMap stations={this.props.stats}
                    callbackFromParent={this.myCallback}
        />
      </div>)
  };
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
    stats: state.stats
  }
}

const mapDispatchToProps = {
  fetchUsersSuccess,
  fetchStatSuccess
}

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
