import React from 'react';
import '../App.css';
import NavBar from './NavBar';
import { Route, Switch } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './Dashboard';
import Loading from "./loading.js"

class App extends React.Component {

  state = {
    scores: {}
  }

  componentDidMount() {
    console.log('working')
    this.getClimateScores()
    this.timerId = setInterval(() => this.getClimateScores(), 10000)
    console.log(this.state.scores)
  }

  componentWillUnmount() {
    clearInterval(this.timerId)
  }

  getClimateScores() {
    fetch(`https://climate-score.p.rapidapi.com/41.77/-87.34`, {
        "method": "GET",
        "headers": {
        "x-rapidapi-host": "climate-score.p.rapidapi.com",
        "x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
      }
    })
    .then(result => result.json())
    .then(result => {
      this.setState({ 
        scores: {
          ...this.state.scores, result 
        }
      })
    })
  }

  render() {
    return (
      <>
       Hello
      </>
    )
  }
}

export default App

