import React, { Component } from "react";
import { Dropdown, Checkbox, Button, Icon, Card, Image, Grid, Segment } from 'semantic-ui-react'
import { connect } from 'react-redux'

export class UserLocations extends Component {

renderLocations = () => {
  this.props.locations.map((location)=>{
    return <div>
      <h4>{location.name}</h4>
      <Button>Delete</Button>
    </div>
  })
}

render(){
  console.log(this.state)
  console.log(this.props)
  return <Card  fluid color='blue'>
  <Card.Content extra>
    <a>
      <Icon name='user' />
      Add Current Location
    </a>
  </Card.Content>
  {this.renderLocations()}
  </Card>
}

}

const mapStateToProps = (state) => {
  return {
    locations: state.locations,
  }
}

export default connect(mapStateToProps, null)(UserLocations);
