import React, { Component } from "react";

export class ClimateScore extends Component {

  state = {
    ClimateScore: 33,
    DroughtScore: 67,
    FireScore: 33,
    SeaLevelScore: 0,
    StormScore: 63,
    TempScore: 1,
  }

  componentDidUpdate(){
    fetch(`https://climate-score.p.rapidapi.com/${this.props.coords.lat}/${this.props.coords.lon}`, {
    	"method": "GET",
    	"headers": {
	  	"x-rapidapi-host": "climate-score.p.rapidapi.com",
	  	"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
      }
    })
    .then(resp => resp.json())
    .then(data => {
      this.renderClimateScore(data)
    })
    .catch(err => {console.log(err)})
  }

  renderClimateScore = (data) => {
    return <div>
      <p>{data.ClimateScore}</p>
      </div>
  }

  render() {
    console.log(this.props)
    return <div>
      {this.renderClimateScore()}
      <p>Latitude: {this.props.coords.lat + '------'}Longitude: {this.props.coords.lon}</p>
    </div>
  }

} 

export default ClimateScore
