import React, { Component } from "react";
import {Map, InfoWindow, Marker, MarkerWithLabel, GoogleApiWrapper, } from 'google-maps-react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import 'react-google-places-autocomplete/dist/index.min.css';
import Geocode from "react-geocode";
import ClimateScore from './ClimateScore'
import { Dropdown, Checkbox } from 'semantic-ui-react'
import mapStyles from './mapStyles'
import MoreDetails from './MoreDetails'
import useMouse from '@react-hook/mouse-position'
import InfoWindowTest from './InfoWindowTest'



class MarkersList extends React.Component {

  constructor(props) {
    super(props);
    this.markersRendered = false;
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (JSON.stringify(this.props.places) === JSON.stringify(nextProps.places) && this.markersRendered) {
      return false;
    }
    this.markersRendered = true;
    return true;
  }

  render() {
    return (
      <span>
        {this.props.places.map((place, i) => {
          return (
            <Marker
              {...this.props}
              key={i}
              data={place}
              position={{ lat: place.lat, lng: place.lng }}
            />
          );
        })}
      </span>
    )
  }

}

class ClimateMap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showInfoWindow: false,
      activeMarker: {},
      selectedPlace: {}
    };
  }

  handleMarkerClick = (markerProps, marker, e) => {
    this.setState({
      selectedPlace: markerProps.data,
      activeMarker: marker,
      showInfoWindow: true
    });
  };


  render() {
    return (
      <div className="map-container">
        <Map
          google={this.props.google}
          className={"map"}
          zoom={4}
          initialCenter={this.props.center}
        >
          <MarkersList google={this.props.google} places={this.props.places} onClick={this.handleMarkerClick} />
          <InfoWindow
            ref={this.infoWindowRef}
            marker={this.state.activeMarker}
            visible={this.state.showInfoWindow}
          >
            <h3>{this.state.selectedPlace.name}</h3>
          </InfoWindow>
        </Map>
      </div>
    );
  }
}



