import React from 'react';
import { connect } from 'react-redux'
import { fetchUsersSuccess } from '../actions/user'
import { fetchStatSuccess } from '../actions/stat'
import { fetchCitiesSuccess } from '../actions/city'
import { currentUser } from '../actions/auth'
import ClimateMap from './ClimateMap'
import climScores from './climScores.json'

class Dashboard extends React.Component {

  state = {
    climateScores: climScores,
  }

  componentDidMount(){const token = localStorage.getItem('token')
    console.log(token)
    if (!token) {
      this.props.history.push('/login')
    } else {
      const reqObj = {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
      fetch('http://localhost:3001/current_user', reqObj)
      .then(resp => resp.json())
      .then(data => {
        this.props.currentUser(data)
      })
      fetch('http://localhost:3001/users')
      .then(resp => resp.json())
      .then(data => {
        this.props.fetchUsersSuccess(data)
      })
      fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
      .then(resp => resp.json())
      .then(stations => {
        let x = stations.data.slice(0,1000)
        this.props.fetchStatSuccess(stations.data)
      })
      fetch('https://public.opendatasoft.com/api/records/1.0/search/?dataset=1000-largest-us-cities-by-population-with-geographic-coordinates&q=&rows=1000&sort=-rank&facet=city&facet=state')
      .then(resp => resp.json())
      .then(cities => {
        this.props.fetchCitiesSuccess(cities.records)
      })
    }
  }



  render(){ 
    return (
      <div>

        <ClimateMap stations={this.props.stats} cities={this.props.cities} climateScores={this.state.climateScores}/>
      </div>)
  };
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
    stats: state.stats,
    cities: state.cities,
  }
}

const mapDispatchToProps = {
  fetchUsersSuccess,
  fetchStatSuccess,
  fetchCitiesSuccess
}

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
