import React, { Component } from "react";
import {InfoWindow, GoogleApiWrapper} from 'google-maps-react';



export class MoreDetails extends Component {


  state={
    lat: '',
    lng: '',
    data: false
  }

  componentDidMount(){
    console.log(this.state)
    if (this.state.lat !== ''){
      console.log('working!')
      fetch(`https://api.waqi.info/feed/geo:${this.state.lat};${this.state.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
      .then(resp => resp.json())
      .then(data => {

        this.setState({
          data: data
        })
        console.log(data)}) 
        this.interval = setInterval(() => this.setState({ time: Date.now() }), 500);
    } else {
        this.interval = setInterval(() => this.setState({ time: Date.now() }), 500);
        this.setState({
          coords: null
        })
      }
    }

  fetchData = () => {
    if (this.state.lat !== ''){
      console.log('working!')
      fetch(`https://api.waqi.info/feed/geo:${this.state.lat};${this.state.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
      .then(resp => resp.json())
      .then(data => {
        console.log(data)
        this.setState({
          data: data.data
        })
      })
    } else {
      console.log('more details state of lat is empty string lol')
      return null
    }
  }


  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render(){
    this.fetchData()
    console.log(this.state)
    if (this.state.data === false){
      console.log('no pin hover yet...')
      return <div><p>awaiting pin hover!</p></div>
    } else {
      console.log(this.state.data)
    return <div>
      <p>No2: {this.state.data.iaqi.no2 ? this.state.data.iaqi.no2.v : null} pm10: {this.state.data.iaqi.no2.v}</p>
      </div>
    }
  }

}

export default MoreDetails
