import React, { Component } from "react";
import {InfoWindow, GoogleApiWrapper} from 'google-maps-react';



export class MoreDetails extends Component {


  state={
    lat: false,
    lng: '',
    data: false,
    clientX: '50%',
    clientY: '50%',
    display: 'none'
  }

  componentDidMount(){
    this.interval = setInterval(() => this.setState({ time: Date.now() }), 50);
  }

  componentWillUnmount() {       
    clearInterval(this.interval);
  }

  renderNo2 = () => {
    if (this.state.data !== false && this.state.data.iaqi.hasOwnProperty('no2')){
      return <div>
        <p>No2: {this.state.data.iaqi.no2.v}</p>
      </div>
    } else {
      return <p>-</p>
    }
  }

  fetchData = () => {
    if (this.state.lat){
      fetch(`https://api.waqi.info/feed/geo:${this.state.lat};${this.state.lng}/?token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c`)
      .then(resp => resp.json())
      .then(data => {
        this.setState({
          data: data.data
        })
      })
    } else {
      return null
    }
  }

  render(){
    const style = {
      display: this.state.display,
      position: 'absolute',
      top: '50%',
      left: '90%',
      // top: this.state.clientY - 40,
      // left: this.state.clientX,
      transform: 'translateX(-50%)',
      width: '100px',
      height: '100px',
      'z-index': '10',
      color: '#000080',
      'user-select': 'none',
      '-moz-user-select': 'none',
      '-khtml-user-select': 'none',
      '-webkit-user-select': 'none',
      '-o-user-select': 'none',
      'background-color': '#FFFFFF'
    }
    this.fetchData()
    return <div style={style}>
      {/* {this.state.data ? this.state.data.iaqi.dew.v : <p>-</p>} */}
      {this.renderNo2()}
    </div>
  }
}



export default MoreDetails
