import React, { Component } from "react";
import {Marker} from 'google-maps-react';

export class AQIMarkers extends Component {

  state = {
    stations: []
  }

  componentDidMount(){
    fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
    .then(resp => resp.json())
    .then(stations => {
      this.setState({ 
        stations: stations.data
      })
    })
  }

  renderMarkers = () => {
    console.log('working')
    return this.state.stations.map((statObj)=>{
      return <Marker position={{lat: statObj.lat, lng: statObj.lon}}/>
    })
  }

   render() {

    return <div>
    {this.renderMarkers()}
    </div>


   }
}   

export default AQIMarkers
