import React, { Component } from "react";
import { Dropdown, Checkbox, Button, Icon, Card, Image, Grid, Segment } from 'semantic-ui-react'
import { connect } from 'react-redux'
import { deleteListSuccess } from '../actions/location'

export class UserLocations extends Component {

  renderLocations = () => {
    this.props.locations.map((location)=>{
      return <div>
        <h4>{location.name}</h4>
        <Button>Delete</Button>
      </div>
    })
  }

  handleDelete = (id) => {
    console.log('i was clicked!')
    const reqObj = {
      method: 'DELETE',
      headers: {
        'Content-Type':  'application/json',
      }
    }
    fetch(`http://localhost:3001/api/v1/locations/${id}`, reqObj)
    .then(resp => resp.json())
    .then(data => {
      this.props.deleteLocationSuccess(id)
    })
  }

  render(){
    console.log(this.state)
    console.log(this.props)
    return this.props.locations.map((location)=>{
      return <div>
        <span style={{'font-size': '18px'}}>{location.name}</span><Button onClick={this.handleDelete(this.props.id)}>Delete Location</Button>
      </div>
    })
  }

}

const mapStateToProps = (state) => {
  return {
    locations: state.locations,
  }
}

const mapDispatchToProps = {
  deleteLocationSuccess
}

export default connect(mapStateToProps, mapDispatchToProps)(UserLocations);
