import React, { Component } from "react";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import ClimateScore from './ClimateScore'

export class ClimateMap extends Component {

  state = {
    searchText: '',
    showClimateScore: false,
    coords: {
      lat: '40.6499541',
      lon: '-73.8512693',
    },
    allScores: {
      ClimateScore: 33,
      DroughtScore: 67,
      FireScore: 33,
      SeaLevelScore: 0,
      StormScore: 63,
      TempScore: 1
    } 
  }

  mapCLicked = (mapProps, map, event) => {
    console.log('I was clicked!')
    console.log(mapProps)
    console.log(map)
    console.log(event)
  }


  onMarkerDragEnd = (coord) => {
    const { latLng } = coord;
    const newLat = latLng.lat();
    const newLon = latLng.lng();
    fetch(`https://climate-score.p.rapidapi.com/${newLat}/${newLon}`, {
    	"method": "GET",
    	"headers": {
	  	"x-rapidapi-host": "climate-score.p.rapidapi.com",
	  	"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
      }
    })
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
      this.setState({
        allScores: {
          ClimateScore: data.ClimateScore,
          DroughtScore: data.DroughtScore,
          FireScore: data.FireScore,
          SeaLevelScore: data.SeaLevelScore,
          StormScore: data.StormScore,
          TempScore: data.TempScore,
        },
      })
    })
    .catch(err => {console.log(err)})
    this.setState({
      coords: {
        lat: newLat,
        lon: newLon
      }
    })
  };

  handleChange = (e) => {
    this.setState({
      searchText: e.target.value
    })
  }

  handleSubmit = () => {
    console.log('o')
  }

  render() {
    console.log(this.state.allScores)
    const style = {
      width: '90%',
      height: '90%'
    }
    return (
      <div>
      <ClimateScore coords={this.state.coords} scores={this.state.allScores} />
        <form onSubmit={this.handleSubmit}>
            <input type='text' value={this.state.text} onChange={this.handleChange} />
            <input type='submit' value='Search!'/>
        </form>
      <Map  google={this.props.google}
            style={style} 
            onClick={this.mapCLicked} 
            zoom={14}
            >

        <Marker onClick={this.onMarkerClick}
                name={'Current location'} 
                draggable={true}
                onDragend={(t, map, coord) => this.onMarkerDragEnd(coord)}/>

        <InfoWindow onClose={this.onInfoWindowClose}>
        </InfoWindow>
      </Map>
      </div>
    );
  }
}




export default GoogleApiWrapper({
  apiKey: ('AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8')
})(ClimateMap)
