import React from 'react';
import '../App.css';
import NavBar from './NavBar';
import { Route, Switch } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './Dashboard';
import Loading from "./loading.js"
import cities from './cities.json'
import climScores from './climScores.js'

class App extends React.Component {

  state = {
    scores: [],
    coordsAndScores: [],
    cities: cities.records,
    one: 991,
    finalScores: climScores
  }

  componentDidMount() {
    console.log(this.state)
    // this.getClimateScores()
    // this.timerId = setInterval(() => this.getClimateScores(), 3000)
    console.log(this.state.scores)
  }

  componentWillUnmount() {
    clearInterval(this.timerId)
  }

  getClimateScores = () => {
    if (this.state.one === 1){ 
    const lat = this.state.cities[this.state.one].fields.coordinates[0]
    const lon = this.state.cities[this.state.one].fields.coordinates[1]
    fetch(`https://climate-score.p.rapidapi.com/${lat}/${lon}`, {
	  "method": "GET",
	  "headers": {
		"x-rapidapi-host": "climate-score.p.rapidapi.com",
		"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
  	}
    })
    .then(resp => resp.json())
    .then(dataa => {
      console.log(dataa)
	    this.setState({ 
        scores: [
          ...this.state.scores, 
          {...dataa, lat:lat,lng: lon}
        ],
        one: this.state.one + 1
      })
    })
    } else if (this.state.one !== 1001) {
    console.log(this.state.one)
    console.log(this.state)
    const lat = this.state.cities[this.state.one].fields.coordinates[0]
    const lon = this.state.cities[this.state.one].fields.coordinates[1]
    console.log(lat,lon)
    fetch(`https://climate-score.p.rapidapi.com/${lat}/${lon}`, {
	  "method": "GET",
	  "headers": {
		"x-rapidapi-host": "climate-score.p.rapidapi.com",
		"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
  	}
    })
    .then(resp => resp.json())
    .then(dataa => {
	    this.setState({ 
        scores: [
          ...this.state.scores, 
          {...dataa, lat:lat,lng: lon}
        ],
        one: this.state.one + 1
        })
      })
    }
  }

  render() {
    console.log(this.state)
    console.log(this.state.scores)
    console.log(JSON.stringify(this.state.scores))
    return (
      <>
       Hello
      </>
    )
  }
}

export default App

