import React from 'react';
import { connect } from 'react-redux'
import { fetchLocationsSuccess } from '../actions/location'
import { fetchStatSuccess } from '../actions/stat'
import { fetchCitiesSuccess } from '../actions/city'
import { currentUser } from '../actions/auth'
import ClimateMap from './ClimateMap'
import Geocode from "react-geocode";
import climScores from './climScores.json'
import NewsCard from './NewsCard';
import Grid from '@material-ui/core/Grid';

class NewsFeed extends React.Component {

  state = {
    newsArticles: []
  }

  componentDidMount(){
    const token = localStorage.getItem('token')
    console.log(token)
    if (!token) {
      this.props.history.push('/login')
    } else {
      const reqObj = {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
      fetch('http://localhost:3001/current_user', reqObj)
      .then(resp => resp.json())
      .then(data => {
        console.log(data)
        this.props.currentUser(data)
      })
      fetch('https://newsapi.org/v2/everything?q=climate%20change&apiKey=cdcfd508f0a3409cb71b20f0d5bba38e')  
      .then(resp => resp.json())
      .then(news => {
        console.log(news.articles)
        this.setState({
          newsArticles: news.articles
        })
      })
    }
  }

  renderFirst3rd = () => {
    const length = this.state.newsArticles.length
    const one3rd = length / 3
    const roundedUp = Math.ceil(one3rd)
    const first3rd = this.state.newsArticles.slice(0, roundedUp)
    return first3rd.map((article)=>{
      return <NewsCard />
    })
  }

  renderSecond3rd = () => {
    const length = this.state.newsArticles.length
    const one3rd = length / 3
    const roundedUp = Math.ceil(one3rd) + 1 
    console.log(roundedUp)
    // const second3rd = this.state.newsArticles.slice(roundedUp)
  }

  render(){ 
      return (
        <div>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="flex-start"
            spacing={0}
            >
          <Grid item xs>
            {this.renderFirst3rd()}
            </Grid>
            <Grid item xs>
            {this.renderSecond3rd()}
            </Grid>
            <Grid item xs>
            <NewsCard/>
            </Grid>


          </Grid>
        </div>)
  };
}

const mapStateToProps = (state) => {
  return {
    auth: state.auth,
    current_user: state.currentUser,
  }
}

const mapDispatchToProps = {
  currentUser
}

export default connect(mapStateToProps, mapDispatchToProps)(NewsFeed);
