import React from 'react';
import '../App.css';
import NavBar from './NavBar';
import { Route, Switch } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './Dashboard';
import Loading from "./loading.js"
import cities from './cities.json'

class App extends React.Component {

  state = {
    scores: {},
    coordsAndScores: {},
    cities: []
  }

  componentDidMount() {
    fetch('https://public.opendatasoft.com/api/records/1.0/search/?dataset=1000-largest-us-cities-by-population-with-geographic-coordinates&q=&rows=1000&sort=-rank&facet=city&facet=state')
      .then(resp => resp.json())
      .then(cities => {
        this.setState({
          cities: [this.state.cities,cities.records]
        })
      })
    console.log(this.state.cities)
    this.state.cities.map((city)=>{
      this.setState({
        coordsAndScores: [...this.state.coordsAndScores,
                          city.fields.co]
      })
    })
    this.getClimateScores()
    this.timerId = setInterval(() => this.getClimateScores(), 10000)
    console.log(this.state.scores)
  }

  componentWillUnmount() {
    clearInterval(this.timerId)
  }

  getClimateScores = () => {

    fetch("https://climate-score.p.rapidapi.com/40.6499541/-73.8512693", {
	  "method": "GET",
	  "headers": {
		"x-rapidapi-host": "climate-score.p.rapidapi.com",
		"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
  	}
    })
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
	    this.setState({ 
        scores: {
          ...this.state.scores, data
        }
      })
    })

  }

  render() {
    return (
      <>
       Hello
      </>
    )
  }
}

export default App

