import React from 'react';
import '../App.css';
import NavBar from './NavBar';
import { Route, Switch } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './Dashboard';
import Loading from "./loading.js"
import cities from './cities.json'

class App extends React.Component {

  state = {
    scores: [],
    coordsAndScores: [],
    cities: cities.records
  }

  componentDidMount() {
    console.log(this.state)
    this.getClimateScores()
    this.timerId = setInterval(() => this.getClimateScores(), 10000)
    console.log(this.state.scores)
  }

  componentWillUnmount() {
    clearInterval(this.timerId)
  }

  getClimateScores = () => {
    let i = 1
    const lat = this.state.cities[i].fields.coordinates[0]
    const lon = this.state.cities[i].fields.coordinates[1]
    fetch(`https://climate-score.p.rapidapi.com/${lat}/${lon}`, {
	  "method": "GET",
	  "headers": {
		"x-rapidapi-host": "climate-score.p.rapidapi.com",
		"x-rapidapi-key": "c5855f8358mshe884588b34ae70ep1a1627jsn5e1e97c23a80"
  	}
    })
    i = i + 1
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
	    this.setState({ 
        scores: [
          ...this.state.scores, data
        ]
      })
    })

  }

  render() {
    console.log(this.state.cities)
    console.log(this.state.scores)
    console.log(this.state.coordsAndScores)
    return (
      <>
       Hello
      </>
    )
  }
}

export default App

