import React from 'react';
import { connect } from 'react-redux'
import { fetchUsersSuccess } from '../actions/user'
import { fetchStatSuccess } from '../actions/stat'
import { fetchCitiesSuccess } from '../actions/city'
import ClimateMap from './ClimateMap'
import cities from './cities.json'


class Dashboard extends React.Component {

  componentDidMount(){

      fetch('http://localhost:3001/users')
      .then(resp => resp.json())
      .then(data => {
        this.props.fetchUsersSuccess(data)
      })
      fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
      .then(resp => resp.json())
      .then(stations => {
        this.props.fetchStatSuccess(stations.data)
      })
      fetch('https://public.opendatasoft.com/api/records/1.0/search/?dataset=1000-largest-us-cities-by-population-with-geographic-coordinates&q=&rows=1000&sort=-rank&facet=city&facet=state')
      .then(resp => resp.json())
      .then(cities => {
        console.log(cities.records)
        this.props.fetchCitiesSuccess(cities.records)
      })
  }

  render(){ 
    return (
      <div>

        <ClimateMap stations={this.props.stats} cities={this.props.cities}/>
      </div>)
  };
}

const mapStateToProps = (state) => {
  return {
    users: state.users,
    stats: state.stats,
    cities: state.cities
  }
}

const mapDispatchToProps = {
  fetchUsersSuccess,
  fetchStatSuccess,
  fetchCitiesSuccess
}

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
