import React from 'react';
import '../App.css';
import NavBar from './NavBar';
import { Route, Switch, useRouteMatch, useLocation } from 'react-router-dom';
import { BrowserRouter } from 'react-router-dom';
import Dashboard from './Dashboard';
import Loading from "./loading.js"
import Login from './Login';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { Link } from 'react-router-dom';


function App() {
  const match = useRouteMatch()
  const location = useLocation()

  return (

    <BrowserRouter>
    <div className="App">
    {/* <NavBar icon="logo" title="ClyMate" description="climate app" /> */}
    {/* <Loading /> */}
    <Tabs value={location.pathname} >
      <Tab label="Dashboard" component={Link} to={`${match.url}/dashboard`} value={`${match.url}/dashboard`} />
      <Tab label="Audit Results" component={Link} to={`${match.url}/audit-results`} value={`${match.url}/audit-results`} />
    </Tabs>
      <Switch>
        <Route show={false} path={`${match.url}/home`} component={Dashboard} />
        <Route path={`${match.url}/login`} component={Login} />
      </Switch>
    </div>
    </BrowserRouter>
  );
}

export default App;
