import React, { Component } from "react";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';

export class ClimateMap extends Component {

  state = {
    showClimateScore: false
  }

  mapCLicked = (mapProps, map, event) => {
    console.log('I was clicked!')
    console.log(mapProps)
    console.log(map)
    console.log(event)
  }


  onMarkerDragEnd = (coord) => {
    this.setState({
      showClimateScore: !this.state.showClimateScore
    })
    console.log(coord)
    const { latLng } = coord;
    const lat = latLng.lat();
    const lng = latLng.lng();
    console.log(lat)
    console.log(lng)
  };

  renderClimateScore = () => {
    console.log(this.state.showClimateScore)
    return <div>
      <h1>WORKING</h1>
    </div>
  }

  render() {
    return (
      <div>
      <Map google={this.props.google} onClick={this.mapCLicked} zoom={14}>

        <Marker onClick={this.onMarkerClick}
                name={'Current location'} 
                draggable={true}
                onDragend={(t, map, coord) => this.onMarkerDragEnd(coord)}/>

        <InfoWindow onClose={this.onInfoWindowClose}>
        </InfoWindow>
      </Map>
      {this.state.showClimateScore ? this.renderClimateScore() :null}
      </div>
    );
  }
}




export default GoogleApiWrapper({
  apiKey: ('AIzaSyDmc1KD6Xr80d3hduc4Q2MObw1uotQuY-8')
})(ClimateMap)
