import React, { Component } from "react";
import {Marker} from 'google-maps-react';

export class AQIMarkers extends Component {

  renderMarkers = () => {
    fetch('https://api.waqi.info/map/bounds/?latlng=85,-180,-85.05115,180&token=87b2bba6a5b2e26c577ffc48e297eaed82a8408c')
    .then(resp => resp.json())
    .then(stations => {
      this.setState({ 
        allAqiStations: stations.data
      })
    })
    console.log('lol')
    return this.state.allAqiStations.map((station) => {
      return <Marker position={{lat: station.lat, lng: station.lon}}/>
    })
  }

  render(){
    return <div>
      {this.renderMarkers()}
    </div>
  }

}

export default AQIMarkers
